mapper.select:
select
l.WLBH, l.CJSJ, l.DBSJ, l.CJR,(select xm from gy_zgxx where zgid = l.cjr and rownum= 1) as cjrxm,l.DBR, l.YSSJ, l.YSR, l.BQDM, l.DDSJ, l.BBSL, l.WLZT, l.BQMC, l.QSR
from ZHLIS.L_LOGISTICS l


        and WLBH = #{wlbh}


        and CJSJ = #{cjsj}


        and DBSJ = #{dbsj}


        and CJR = #{cjr}


        and DBR = #{dbr}


        and YSSJ = #{yssj}


        and YSR = #{ysr}


        and BQDM = #{bqdm}


        and DDSJ = #{ddsj}


        and BBSL = #{bbsl}


        and WLZT = #{wlzt}


        and BQMC = #{bqmc}


        and QSR = #{qsr}


        and WLBH like concat(concat('%',#{searchValue,jdbcType=VARCHAR}),'%')


        and CJSJ >= to_date(#{params.beginTime},'yyyy-MM-dd HH24:mi:ss')


        and CJSJ <= to_date(#{params.endTime},'yyyy-MM-dd HH24:mi:ss')


order by cjsj desc

select
WLBH, CJSJ, DBSJ, CJR, DBR, YSSJ, YSR, BQDM, DDSJ, BBSL, WLZT, BQMC, QSR,(select xm from gy_zgxx where zgid = l.cjr and rownum= 1) as cjrxm
from ZHLIS.L_LOGISTICS


        and WLBH = #{wlbh}


        and CJSJ = #{cjsj}


        and DBSJ = #{dbsj}


        and CJR = #{cjr}


        and DBR = #{dbr}


        and YSSJ = #{yssj}


        and YSR = #{ysr}


        and BQDM = #{bqdm}


        and DDSJ = #{ddsj}


        and BBSL = #{bbsl}


        and WLZT = #{wlzt}


        and BQMC = #{bqmc}


        and QSR = #{qsr}


        and WLBH like concat(concat('%',#{searchValue,jdbcType=VARCHAR}),'%')


        and CJSJ >= to_date(#{params.beginTime},'yyyy-MM-dd HH24:mi:ss')


        and CJSJ <= to_date(#{params.endTime},'yyyy-MM-dd HH24:mi:ss')


order by cjsj desc
